import { ref } from 'vue';
export default {
  setup(_, {
    emit
  }) {
    const title = ref('');
    const description = ref('');
    const submitForm = () => {
      emit('addTask', {
        title: title.value,
        description: description.value
      });
      title.value = '';
      description.value = '';
    };
    return {
      title,
      description,
      submitForm
    };
  }
};
